select CourseOffers.courseOfferId, Courses.CourseId, CourseOffers.Year, Quartile, CourseName, CourseDescription, Degrees.DegreeId, ECTS, Degrees.Dept, Degrees.DegreeDescription, Degrees.TotalECTS, Teachers.TeacherId, Teachers.TeacherName, Teachers.Address, Teachers.BirthyearTeacher, Teachers.Gender from CourseOffers, Courses, Degrees, TeacherAssignmentsToCourses, Teachers where CourseOffers.CourseOfferId = 1 and CourseOffers.CourseId = Courses.courseId and Degrees.DegreeId=Courses.DegreeId and TeacherAssignmentsToCourses.CourseOfferId = CourseOffers.CourseOfferId and Teachers.TeacherId = TeacherAssignmentsToCourses.TeacherId;
select StudentAssistants.CourseOfferId, CourseOffers.CourseId, CourseOffers.Year, CourseOffers.Quartile, Students.StudentId, Students.StudentName, Students.Address, Students.BirthyearStudent, Students.Gender, StudentRegistrationsToDegrees.DegreeId, Degrees.Dept, Degrees.DegreeDescription, Degrees.TotalECTS from StudentAssistants, CourseOffers, Courses, Degrees, Students, StudentRegistrationsToDegrees where StudentAssistants.StudentRegistrationId = 3 and StudentAssistants.StudentRegistrationId = StudentRegistrationsToDegrees.StudentRegistrationId and StudentAssistants.CourseOfferId = CourseOffers.CourseOfferId and CourseOffers.CourseId = Courses.CourseId and Students.StudentId = StudentRegistrationsToDegrees.StudentId and Degrees.DegreeId = StudentRegistrationsToDegrees.DegreeId;
select avg(grade) from (select StudentRegistrationsToDegrees.StudentId, CourseRegistrations.Grade from StudentRegistrationsToDegrees, CourseRegistrations where StudentRegistrationsToDegrees.StudentRegistrationId = 3 and CourseRegistrations.StudentRegistrationId = StudentRegistrationsToDegrees.StudentRegistrationId) as StudentGrades(StudentId, grade) group by StudentId;
